import React from "react";
import { useParams } from "react-router-dom";
import CounterWrapFour from "../CounterArea/CounterWrapFour";

const ProjectDetailsArea = () => {
  const { projectId } = useParams();
  
  // This would typically come from an API or data store
  const projectData = {
    dms: {
      title: "Dentist Management System",
      description: "A comprehensive dental practice management system that streamlines patient care, appointment scheduling, and administrative tasks. Features include patient records management, appointment scheduling, billing and invoicing, treatment planning, and reporting tools.",
      image: "/img/project/DMC/Screenshot 2025-05-04 at 14.53.53.png",
      technologies: ["React", "Node.js", "MongoDB", "Express"],
      features: ["Patient Management", "Appointment Scheduling", "Billing System", "Treatment Planning"]
    },
    aop: {
      title: "Academic Orientation Platform",
      description: "An innovative platform designed to help students navigate their academic journey. The system provides comprehensive information about university programs, course requirements, and career paths.",
      image: "/img/project/AOP/Screenshot 2025-01-28 at 19.26.25.png",
      technologies: ["React", "Firebase", "Material-UI"],
      features: ["Course Catalog", "Student Dashboard", "Career Guidance", "Interactive Maps"]
    },
    erp: {
      title: "Enterprise Resource Planning",
      description: "A comprehensive enterprise resource planning system that helps businesses manage their operations efficiently. The platform integrates various business processes into a unified system.",
      image: "/img/project/ERP/Screenshot 2025-05-04 at 14.57.21.png",
      technologies: ["React", "Node.js", "PostgreSQL", "Docker"],
      features: ["Resource Management", "Employee Portal", "Inventory Control", "Financial Management"]
    },
    rms: {
      title: "Restaurant Management System",
      description: "A complete restaurant management solution that streamlines operations from order taking to inventory management. The system helps restaurants improve efficiency and customer service.",
      image: "img/project/RMS/Screenshot 2025-01-28 at 19.36.36.png",
      technologies: ["React", "Node.js", "MySQL", "Socket.io"],
      features: ["Order Management", "Inventory Tracking", "Customer Database", "Analytics Dashboard"]
    }
  };

  const project = projectData[projectId] || {
    title: "Project Not Found",
    description: "The requested project could not be found.",
    image: "/img/images/project_details_img.jpg",
    technologies: [],
    features: []
  };

  return (
    <section className="project-details-area pt-40 pb-120">
      <div className="container">
        <div className="row">
          <div className="col-lg-12">
            <div className="project-details-inner">
              <div className="project-details-img">
                <img src={project.image} alt={project.title} />
              </div>
              <div className="project-details-content">
                <h2 className="title">{project.title}</h2>
                <p>{project.description}</p>
                
                {project.technologies.length > 0 && (
                  <div className="technologies-section mt-4">
                    <h3>Technologies Used</h3>
                    <ul className="list-unstyled">
                      {project.technologies.map((tech, index) => (
                        <li key={index}>{tech}</li>
                      ))}
                    </ul>
                  </div>
                )}

                {project.features.length > 0 && (
                  <div className="features-section mt-4">
                    <h3>Key Features</h3>
                    <ul className="list-unstyled">
                      {project.features.map((feature, index) => (
                        <li key={index}>{feature}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>

              <div className="row align-items-center">
                <div className="col-lg-12">
                  {/* counter wrap four */}
                  <CounterWrapFour />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="project-shape-wrap">
        <img src="/img/images/services_details_shape.png" alt="" />
      </div>
    </section>
  );
};

export default ProjectDetailsArea;
